mapper.select:
select apply.apy_sq, apply.apy_dtm, apply.apy_cndtn, (select code_name from P2_TBL_COMMON_CODE_DTL_S where code_id = apply.apy_cndtn) as apy_cndtn_name, resume.rsm_sq, resume.rsm_img_file_url, resume.rsm_fnl_edctn_cd, (select code_name from P2_TBL_COMMON_CODE_DTL_S where code_id = resume.rsm_fnl_edctn_cd) as rsm_fnl_edctn_name, resume.rsm_grd, resume.rsm_es, RESUME.rsm_name, IF(posi.pstn_prpsl_sq is null, "지원", "제안") as rnqns_name from p2_tbl_apply_h as apply left outer join P2_TBL_RESUME_M as resume on apply.rsm_sq = resume.rsm_sq left outer join P2_TBL_POSITION_PROPOSAL_M as posi on posi.rsm_sq = resume.rsm_sq left outer join P2_TBL_MEMBER_M as member on member.mbr_sq = resume.mbr_sq where apply.jbp_sq = #{searchListData.jbp_sq} and apply.apy_cndtn != 403
and apply.apy_cndtn = #{searchListData.condition}
and posi.pstn_prpsl_sq is null
and posi.pstn_prpsl_sq is not null
order by apy_dtm ${searchListData.sort} limit #{paginationData.startRowIndex},#{paginationData.showDataCountPerPage};

mapper.update:
update P2_TBL_APPLY_H
set apy_cndtn = #{apy_cndtn}, intv_dtm = #{intv_dtm}
where apy_sq = #{apy_sq};
